getGenre(User, Genre) :- nth0(0, User, Genre).
getActors(User, Actors) :- nth0(1, User, Actors).
getCollection(User, Collection) :- nth0(2, User, Collection).
getRating(User, Rating) :- nth0(3, User, Rating).
getCompanies(User, Companies) :- nth(4, User, Companies).

checkViability(Parent, Potential) :- length(Parent, Total), intersection(Parent, Potential, Common), length(Common, Num), Portion is (1 / 2) * Total, Num >= Portion.

recommendRating(Rating, Movie) :- film(Name, _, _, _, MRating, _), MRating >= Rating, Movie = Name.
recommendGenre(Genre, Movie) :- film(Name, MGenre, _, _, _, _), checkViability(Genre, MGenre), Movie = Name.
recommendActors(Actors, Movie) :-film(Name, _, MActors, _, _, _), checkViability(Actors, MActors), Movie = Name.
recommendCollection(Collection, Movie) :- film(Name, _, _, Mcollection, _, _), MCollection == Collection, Movie = Name.
recommendCompanies(Companies, Movie) :- film(Name, _, _, _, _, MCompanies), checkViability(Companies, MCompanies), Movie = Name.

recommendAll(User, Movie) :- getGenre(User, Genre), getActors(User, Actors), getCollection(User, Collection), getRating(User, Rating), getCompanies(User, Companies), film(Name, MGenre, MActors, Collection, MRating, MCompanies),  Rating >= MRating, checkViability(Genre, MGenre), checkViability(Actors, MActors), checkViability(Companies, MCompanies), Movie = Name.

